class SimpleServlet extends Servlet {
	Foo myFoo = null;

	public void doGet(HttpServletRequest req, HttpServletRespose res) {
		if (myFoo == null) {
			myFoo = new Foo();
		}
		myFoo.setValue(1);
		int value = myFoo.getValue(); 	// could be 1 or 2 depending on other threads 
		myFoo.setValue(2);
	}
}
